import React from 'react';
import './Car.css';



class Car extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ovnerName: this.props.ovnerName || 'sosi',
            carLable: 'mazda',
            carEngine: 'Заведен',
            carDriveMotion: 'Едет',
            carWheels: 'Крутятся' 
        };
        this.carPusk = this.carPusk.bind(this)
        this.carEngineOff = this.carEngineOff.bind(this)
        this.carDriveEffect = this.carDriveEffect.bind(this)
        
    }


    carPusk(e) {
     e.preventDefault();
        this.setState(state => ({
         carEngine: 'Заведен'}));
    }

    carEngineOff(e) {
     e.preventDefault();
        this.setState(state => ({
        carEngine: 'Заглушон',carDriveMotion: 'Стоит', carWheels: 'Стоят' }));
    }
    carDriveEffect(e) {
     e.preventDefault();
     this.state.carEngine ? 'Заведен' :    
     this.setState(state => ({
        carDriveMotion: 'Едет' }));
    }



    render() {
        return (
            <div>
                <div>{this.state.ovnerName}</div>
                <div>{this.state.carLable}</div>
                <div>{this.state.carEngine}</div>
                <div>{this.state.carDriveMotion}</div>
                <div>{this.state.carWheels}</div>
                <button onClick={this.carPusk}>Завести</button>
                <button onClick={this.carEngineOff}>Заглушить</button>
                <button onClick={this.carDriveEffect}>Поехать</button>
                <button >Остановиться</button>
            </div>
        );
    }
}






export default Car;
