import React from 'react';
import logo from './logo.svg';
import './App.css';
import Car from './Car/Car';



class Clock extends React.Component {       // Специальная функция такая же как function
  constructor(props) {                      //Метод constructor — специальный метод, необходимый для создания и инициализации объектов, созданных, с помощью класса. 
    super(props);                           //Ключевое слово super используется для вызова функций, принадлежащих родителю объекта.
    this.state = {                          // Массив в объектами
      cars: [{
        ovnerName: 'sosi',
        carLable: 'mazda',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'da',
        carLable: 'lada',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'net',
        carLable: 'honda',
        carEngine: false,
        carDriveMotion: false,
        carWheels: false
      }],
     formState: {ovnerName: '' , carLable: '', lableOptions: ['Mazda', 'Vesta', 'Shoha'], accepteCheck: false , }   
   }; 
   console.log(this.state)                                 
   this.handleChangeName = this.handleChangeName.bind(this);
   this.handleChangeLable = this.handleChangeLable.bind(this);             
   this.handleSubmit = this.handleSubmit.bind(this) 
  }



  handleSubmit(e) { 
    alert(`${this.state.formState.ovnerName}, ${this.state.formState.carLable}` )
    e.preventDefault();
  }

  handleChangeName (e) {
    e.preventDefault();
    var val = e.target.value;
    this.setState( prevState => ({ formState : 
      {...prevState.formState, ovnerName: val
      }
    }))
}


  handleChangeLable (e) {
    e.preventDefault();
    var val = e.target.value;
    this.setState( prevState => ({ formState : 
      {...prevState.formState, carLable: val
      }
    }))
  }





  render() {                                        

    return (                                        
      <div>
        <h1>Привет, мир!</h1>
        <div>
          {this.state.cars.map((car) => {           
            return <Car
              ovnerName={car.ovnerName}             
              carLable={car.carLable}
              carEngine={car.carEngine}
              carDriveMotion={car.carDriveMotion}
              carWheels={car.carWheels}
            />
          })}
          <form  onSubmit={this.handleSubmit}>      
            <label>
              Имя владельца:
               <input type="text" name="ovnerName" value={this.state.formState.ovnerName} onChange ={this.handleChangeName}/>
                <br />Марка машины:
               <input type="text" name="carLable" value={this.state.formState.carLable} onChange ={this.handleChangeLable} />
                <br />Марка машины:
                <select
              name='lable'
              value=''
              onChange=''
              >
              <option value="" disabled />
              {this.state.formState.lableOptions.map(option => {
                return (
                  <option
                    key={option}
                    value={option}
                    label={option}>{option}
                  </option>
                );
              })}
            </select>
            </label>
            <input type="submit" value="Submit" />
          </form>

        </div>
      </div>
    );
  }
}

export default Clock;
