import React from 'react';
import logo from './logo.svg';
import './App.css';
import Car from './Car/Car';



class Clock extends React.Component {       // Специальная функция такая же как function
  constructor(props) {                      //Метод constructor — специальный метод, необходимый для создания и инициализации объектов, созданных, с помощью класса. 
    super(props);                           //Ключевое слово super используется для вызова функций, принадлежащих родителю объекта.
    this.state = {                          // Массив в объектами
      cars: [{
        ovnerName: 'sosi',
        carLable: 'mazda',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'da',
        carLable: 'lada',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'net',
        carLable: 'honda',
        carEngine: false,
        carDriveMotion: false,
        carWheels: false
      }],
     formState: {name:'вв' , carLable: 'вв', accepteCheck: false , lableOptions: ['1', '2', '3']}   
   }; 
   console.log(this.state)                                 // Выводит заданный в скобках параметр в консоль
   this.handleChange = this.handleChange.bind(this);       // Используется для связки метода класса, если ссылаться на метод без (), например onChange{this.onChange}, требуется привязать метод     
   this.handleSubmit = this.handleSubmit.bind(this) 
  }


  handleChange = (e) => {                                    // Метод или обработчик события
      const {value , name} = e.currentTarget;
      this.setState(({car,formState})=>({
        car: {
          ...car,
          [name]:value
        }
      }))          
  }

  handleSubmit(e) { 

    e.preventDefault();

  }






  render() {                                        // Отрисовка
    return (                                        // Возврат разметки
      <div>
        <h1>Привет, мир!</h1>
        <div>
          {this.state.cars.map((car) => {           // Метод .map с помощью которого мы отрисовываем массив
            return <Car
              ovnerName={car.ovnerName}             // Это нужно для прокидывания props 
              carLable={car.carLable}
              carEngine={car.carEngine}
              carDriveMotion={car.carDriveMotion}
              carWheels={car.carWheels}
            />
          })}
          <form  onSubmit={this.handleSubmit}>      
            <label>
              Имя владельца:
               <input type="text" name="ovnerName"  onChange ={this.handleChange}/>
                <br />Марка машины:
               <input type="text" name="carLable"  onChange ={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>

        </div>
      </div>
    );
  }
}

export default Clock;
