import React from 'react';
import './Car.css';




class Car extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.id,
            ovnerName: this.props.ovnerName || 'default',
            carLable: this.props.carLable || 'stock',
            carEngine: this.props.carEngine || false,
            carDriveMotion: this.props.carDriveMotion || false,
            carWheels: this.props.carWheels || false  
        };
        this.carPusk = this.carPusk.bind(this)
        this.carEngineOff = this.carEngineOff.bind(this)
        this.carDriveEffect = this.carDriveEffect.bind(this)
        this.carStop = this.carStop.bind(this)
    }


    carPusk(e) {
     e.preventDefault();
        this.setState(state => ({
         carEngine: true}));
    }

    carEngineOff(e) {
     e.preventDefault();
        this.setState(state => ({
        carEngine: false, carDriveMotion: false, carWheels: false }));
    }
    carDriveEffect(e) {
     e.preventDefault();
     if (this.state.carEngine === true) {
        this.setState(state => ({
            carDriveMotion: true, carWheels: true}));
      } 
    }

    carStop(e) {
        e.preventDefault();
        this.setState(state => ({
          carDriveMotion: false, carWheels: false}));   
       }

    render() {
        return (
            <div key={this.props.key}>
                <div>{this.state.ovnerName}</div>
                <div>{this.state.carLable}</div>
                <div>{this.state.carEngine ? 'Заведен':'Заглушен'}</div>
                <div>{this.state.carDriveMotion ? 'Едет':'Стоит'}</div>
                <div>{this.state.carWheels ? 'Крутятся' : 'Не крутятся'}</div>
                <button onClick={this.carPusk}>Завести</button>
                <button onClick={this.carEngineOff}>Заглушить</button>
                <button onClick={this.carDriveEffect}>Поехать</button>
                <button onClick={this.carStop}>Остановиться</button>
                <button onClick={this.props.handleDeliteCar} key={this.props.key}>CRASH IT</button>
            </div>
        );
    }
}





export default Car;
