import React from 'react';
import logo from './logo.svg';
import './App.css';
import Car from './Car/Car';



class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cars: [{
        ovnerName: 'sosi',
        carLable: 'mazda',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'da',
        carLable: 'lada',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'net',
        carLable: 'honda',
        carEngine: false,
        carDriveMotion: false,
        carWheels: false
      }],
     formState: {ovnerName:'' , carLable: '', accepteCheck: false , lableOptions: ['1', '2', '3']}   
   }; 

   this.handleChange = this.handleChange.bind(this);
   this.handleSubmit = this.handleSubmit.bind(this)

  }


  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(e) {
    e.preventDefault();
alert()
  }











  render() {
    return (
      <div>
        <h1>Привет, мир!</h1>
        <div>
          {this.state.cars.map((car) => {
            return <Car
              ovnerName={car.ovnerName}
              carLable={car.carLable}
              carEngine={car.carEngine}
              carDriveMotion={car.carDriveMotion}
              carWheels={car.carWheels}
            />
          })}
          <form  onSubmit={this.handleSubmit}>
            <label>
              Имя владельца:
               <input type="text" name="name" value={this.state.formState} onChange ={this.handleChange} />
                <br />Марка машины:
               <input type="text" name="name" />
            </label>
            <input type="submit" value="Submit" />
          </form>

        </div>
      </div>
    );
  }
}








export default Clock;





