import React from 'react';
import logo from './logo.svg';
import './App.css';
import Car from './Car/Car';



class Clock extends React.Component {       // Специальная функция такая же как function
  constructor(props) {                      //Метод constructor — специальный метод, необходимый для создания и инициализации объектов, созданных, с помощью класса. 
    super(props);                           //Ключевое слово super используется для вызова функций, принадлежащих родителю объекта.
    this.state = {                          // Массив в объектами
      cars: [{
        ovnerName: 'sosi',
        carLable: 'mazda',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'da',
        carLable: 'lada',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'net',
        carLable: 'honda',
        carEngine: false,
        carDriveMotion: false,
        carWheels: false
      }],
     formState: {ovnerName:'' , carLable: '', accepteCheck: false , lableOptions: ['1', '2', '3']}   
   }; 
   console.log(this.state)                                 // Выводит заданный в скобках параметр в консоль
   this.onChangeName = this.onChangeName.bind(this);       // Используется для связки метода класса, если ссылаться на метод без (), например onChange{this.onChange}, требуется привязать метод     
   this.handleSubmit = this.handleSubmit.bind(this)
   this.onChangeLable = this.onChangeLable.bind(this)  
  }




  onChangeName = (e) => {                                   
    this.setState({ formState: {ovnerName: e.target.value}});        
  }
  onChangeLable = (e) =>{
    this.setState({ formState: {carLable: e.target.value}});
  }

  handleSubmit(e) { 
    alert(`${this.state.formState.ovnerName}, ${this.state.formState.carLable}`)


    e.preventDefault();

  }





  render() {                                        
    debugger
    return (                                        
      <div>
        <h1>Привет, мир!</h1>
        <div>
          {this.state.cars.map((car) => {           
            return <Car
              ovnerName={car.ovnerName}             
              carLable={car.carLable}
              carEngine={car.carEngine}
              carDriveMotion={car.carDriveMotion}
              carWheels={car.carWheels}
            />
          })}
          <form  onSubmit={this.handleSubmit}>      
            <label>
              Имя владельца:
               <input type="text" name="name" value={this.state.formState.ovnerName} onChange ={this.onChangeName}/>
                <br />Марка машины:
               <input type="text" name="lable" value={this.state.formState.carLable} onChange ={this.onChangeLable} />
            </label>
            <input type="submit" value="Submit" />
          </form>

        </div>
      </div>
    );
  }
}

export default Clock;
