import React from 'react';
import './Car.css';



class Car extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ovnerName: this.props.ovnerName || 'sosi',
            carLable: 'mazda',
            carEngine: true,
            carDriveMotion: true,
            carWheels: true
        };
        this.carPusk = this.carPusk.bind(this)
        this.carDriveMotion = this.carDriveMotion.bind(this)
        
    }

    carPusk(e) {
        e.preventDefault();
        this.setState(state => ({
            carEngine: !state.carEngine
        }));
    }
    
    carDriveMotion(e) {
        e.preventDefault();
        this.setState(state => ({
            carDriveMotion: !state.carDriveMotion
        }));
    }
    
    handleCarDriveEnable() {
        this.setState({carDriveMotion: true, carWheels:true });
      }
    
      handleDriveDisable() {
        this.setState({carDriveMotion: false, carWheels: false})
      }


    render() {
        const carEngine = this.state.carEngine;
        let button;
        if (carEngine) {
          button = <button onClick={this.handleCarDriveEnable} >да</button>;
        } else {
          button = <button onClick={this.handleCarDriveDisable} >нет</button>;
        }
       
        return (
            <div>
                <div carEngine={carEngine}>
                 {button}</div>
                
                
                <div>{this.state.ovnerName}</div>
                <div>{this.state.carLable}</div>
                <div>{this.state.carEngine ? 'Заведён' : 'Заглушён'}</div>
                <div>{this.state.carDriveMotion ? 'Едет' : 'Стоит'}</div>
                <div>{this.state.carcarWheels ? 'Крутятся' : 'Не крутятся'}</div>
                <button onClick={this.carPusk}>Завести</button>
                <button onClick={this.carPusk}>Заглушить</button>
                <button onClick={this.carDriveMotion}>Поехать</button>
                <button >Остановиться</button>
            </div>
        );
    }
}






export default Car;
