import React from 'react';
import logo from './logo.svg';
import './App.css';
import Car from './Car/Car';



class Clock extends React.Component {       // Специальная функция такая же как function
  constructor(props) {                      //Метод constructor — специальный метод, необходимый для создания и инициализации объектов, созданных, с помощью класса. 
    super(props);                           //Ключевое слово super используется для вызова функций, принадлежащих родителю объекта.
    this.state = {                          // Массив в объектами
      cars: [{
        ovnerName: 'sosi',
        carLable: 'mazda',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'da',
        carLable: 'lada',
        carEngine: true,
        carDriveMotion: true,
        carWheels: true
      }, {
        ovnerName: 'net',
        carLable: 'honda',
        carEngine: false,
        carDriveMotion: false,
        carWheels: false
      }],
     formState: {ovnerName: '' ,carLable:'' , lableOptions: ['Mazda', 'Vesta', 'Shoha'], accepteCheck: true , }   
   }; 

   this.handleChangeName = this.handleChangeName.bind(this);            
   this.handleSubmit = this.handleSubmit.bind(this) 
   this.handleChangeLableOptions = this.handleChangeLableOptions.bind(this)
   this.handleCheckboxChange = this.handleCheckboxChange.bind(this)
   this.handleDeliteCar = this.handleDeliteCar.bind(this)
  }



  handleSubmit(e) { 
    var newCar = {
      ovnerName: this.state.formState.ovnerName,
        carLable: this.state.formState.lableOptions,
        carEngine: false,
        carDriveMotion: false,
        carWheels: false
    }

    e.preventDefault();
    this.setState(state =>({
      cars:[...this.state.cars, newCar]
    }))
    console.log(this.state)
  }

  handleChangeName (e) {
    e.preventDefault();
    var val = e.target.value;
    this.setState( prevState => ({ formState : 
      {...prevState.formState, ovnerName: val
      }
    }))
}



handleChangeLableOptions (e){
  e.preventDefault();
   var val = e.target.value;
   this.setState( prevState => ({ formState : 
      {...prevState.formState, lableOptions: val
      }
    }))
}

handleCheckboxChange (e) {

  this.setState( prevState => ({ formState : 
    {...prevState.formState, accepteCheck: !this.state.formState.accepteCheck
    }
  })) 
}

handleDeliteCar (e, i) {
  e.preventDefault();
  var del = this.state.cars;
  del.splice(i, 1);
  this.setState({
    cars: del
  })
}







  render() {                                        

    return (                                        
      <div>
        <h1>Привет, мир!</h1>
        <div>
          {this.state.cars.map((car) => {           
            return <Car 
              handleDeliteCar={this.handleDeliteCar}
              ovnerName={car.ovnerName}             
              carLable={car.carLable}
              carEngine={car.carEngine}
              carDriveMotion={car.carDriveMotion}
              carWheels={car.carWheels}

            />
          })}
          <form  onSubmit={this.handleSubmit} className='car_form' key={i} index={i}>      
            <label>
              Имя владельца:
               <input type="text" name="ovnerName" value={this.state.formState.ovnerName} onChange ={this.handleChangeName}/>
                <br />Марка машины:
                <select value={this.state.formState.lableOptions} onChange={this.handleChangeLableOptions}> 
            <option value='Mazda'>Mazda</option>
            <option value='Vesta'>Vesta</option>
            <option value='Shoha'>Shoha</option>
          </select>
          <br />
          <input type="checkbox" onChange={this.handleCheckboxChange} />
          <span>Согласен с правилами сервиса</span>
            </label>
            <input type="submit" value="Submit" id='submit' disabled={this.state.formState.accepteCheck}  />
          </form>

        </div>
      </div>
    );
  }
}

export default Clock;





