import React from 'react';
import './Car.css';
import CarLogicTest from './CarLogicTest';



class Car extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ovnerName: this.props.ovnerName || 'sosi',
            carLable: 'mazda',
            carEngine: true,
            carDriveMotion: true,
            carWheels: true  
        };
        this.carPusk = this.carPusk.bind(this)
        this.carEngineOff = this.carEngineOff.bind(this)
        this.carDriveEffect = this.carDriveEffect.bind(this)
        this.carStopt = this.carStop.bind(this)
        
    }


    carPusk(e) {
     e.preventDefault();
        this.setState(state => ({
         carEngine: 'Заведен'}));
    }

    carEngineOff(e) {
     e.preventDefault();
        this.setState(state => ({
        carEngine: false,carDriveMotion: false, carWheels: false }));
    }
    carDriveEffect(e) {
     e.preventDefault();
     this.setState(state => ({
       carDriveMotion: true, carWheels: true}));   
    }

    carStop(e){
        e.preventDefault();
        this.setState(state =>({
            carDriveMotion: false, carWheels: false
        }))
    }

    render() {
        return (
            <div>
                <div>{this.state.ovnerName}</div>
                <div>{this.state.carLable}</div>
                <div>{this.state.carEngine ? 'Заведен':'Заглушон'}</div>
                <div>{this.state.carDriveMotion ? 'Едет':'Стоит'}</div>
                <div>{this.state.carWheels ? 'Крутятся' : 'Стоят'}</div>
                <button onClick={this.carPusk}>Завести</button>
                <button onClick={this.carEngineOff}>Заглушить</button>
                <button onClick={this.carDriveEffect}>Поехать</button>
                <button onClick={this.carStop}>Остановиться</button>
            </div>
        );
    }
}






export default Car;
